import '../../../Calendar/data/models/task_model.dart';class UserAccountModel {  String name;  String image;  String password;  late final List<TaskModel> tasks;  UserAccountModel(    this.name,    this.image,    this.password,    this.tasks,  );  // Factory method to create UserAccountModel from JSON  factory UserAccountModel.fromJson(Map<String, dynamic> json) {    return UserAccountModel(      json['name'] as String,      json['image'] as String,      json['password'] as String,      (json['tasks'] as List<dynamic>)          .map((taskJson) =>              TaskModel.fromJson(taskJson as Map<String, dynamic>))          .toList(),    );  }  // Method to convert UserAccountModel to JSON  Map<String, dynamic> toJson() {    return {      'name': name,      'image': image,      'password': password,      'tasks': tasks.map((task) => task.toJson()).toList(),    };  }}