import 'package:hive/hive.dart';import '../../../../core/model/task_model.dart';part 'user_account_model.g.dart';@HiveType(typeId: 2)class UserAccountModel extends HiveObject {  @HiveField(0)  String name;  @HiveField(1)  String image;  @HiveField(2)  String password;  @HiveField(3)  late final List<TaskModel> tasks;  UserAccountModel(    this.name,    this.image,    this.password,    this.tasks,  );  // UserAccountModel.update(  //   this.name,  //   this.image,  //   this.password,  //   this.tasks,  // );  Map<String, dynamic> toMap() {    return {      'name': name,      'image': image,      'password': password,      'tasks': tasks,    };  }  factory UserAccountModel.fromMap(Map<String, dynamic> map) {    return UserAccountModel(      map['name'] as String,      map['image'] as String,      map['password'] as String,      map['tasks'] as List<TaskModel>,    );  }}