import 'package:flutter/material.dart';import 'package:uptodo/core/utils/resource/list.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/icons.dart';import '../../../../core/utils/resource/styles.dart';import '../../data/model/profile_item_model.dart';class BoxOFListProfileItems extends StatelessWidget {  const BoxOFListProfileItems({    super.key,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 24),      child: ListView.builder(        physics: const NeverScrollableScrollPhysics(),        shrinkWrap: true,        itemCount: AppList.listActions.length,        itemBuilder: (context, index) {          return Padding(            padding: const EdgeInsets.symmetric(vertical: 8.0),            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Text(                  AppList.listActions[index].title,                  style: Styles.text14().copyWith(                    color: AppColors.gray1TextColor,                  ),                ),                SizedBox(height: MediaQuery.sizeOf(context).height * .01),                ListView.builder(                  physics: const NeverScrollableScrollPhysics(),                  shrinkWrap: true,                  itemCount: AppList.listActions[index].items.length,                  itemBuilder: (context, indexItem) => _BuildListProfileItems(                    itemModel: AppList.listActions[index].items[indexItem],                  ),                ),              ],            ),          );        },      ),    );  }}class _BuildListProfileItems extends StatelessWidget {  final ItemModel itemModel;  const _BuildListProfileItems({    required this.itemModel,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 8.0),      child: Container(        padding: const EdgeInsets.symmetric(vertical: 8.0),        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            Row(              children: [                Icon(                  itemModel.icon,                  color: AppColors.iconColor,                ),                SizedBox(                  width: MediaQuery.sizeOf(context).width * .015,                ),                SizedBox(                  width: MediaQuery.sizeOf(context).width * .7,                  child: Text(                    itemModel.title,                    maxLines: 1,                    style: Styles.text16(),                  ),                ),              ],            ),            InkWell(              onTap: () {                showAdaptiveDialog(                  context: context,                  builder: (context) {                    return itemModel.screen;                  },                );              },              child: AppIcons.arrowForward,            ),          ],        ),      ),    );  }}