import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/utils/resource/styles.dart';import '../manager/user_cubit.dart';import '../../../../core/utils/resource/string.dart';class ChangeImage extends StatelessWidget {  const ChangeImage({super.key});  @override  Widget build(BuildContext context) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Align(          child: Padding(            padding: const EdgeInsets.symmetric(vertical: 10),            child: Text(AppStrings.changePassword, style: Styles.text16()),          ),        ),        Divider(          height: 5,          indent: 15,          endIndent: 15,        ),        _items(          text: "Tack picture",          onTap: () => context.read<UserCubit>().pickFromCamera(),        ),        _items(          text: "Import from gallery",          onTap: () => context.read<UserCubit>().pickFromGallery(),        ),        _items(          text: "Import from Google Drive",          onTap: () => context.read<UserCubit>().pickFromFile(),        ),      ],    );  }  Widget _items({    required void Function() onTap,    required String text,  }) =>      TextButton(        onPressed: onTap,        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 24),          child: Text(            text,            style: Styles.text16(),          ),        ),      );}