import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/utils/resource/constant.dart';import '../../../../core/utils/resource/styles.dart';import '../../ViewModel/UpdateTask/update_task_cubit.dart';import '../Widget/build_app_bar_task_view.dart';import '../Widget/build_body_view_task.dart';class TaskScreen extends StatelessWidget {  final String id;  const TaskScreen({super.key, required this.id});  @override  Widget build(BuildContext context) {    return BlocBuilder<UpdateTaskCubit, UpdateTaskState>(      builder: (context, state) {        return Scaffold(          appBar: buildAppBarTaskView(context, onTap: () {}),          body: buildBodyView(context),        );      },    );  }  CustomScrollView buildBodyView(BuildContext context) {    return CustomScrollView(          shrinkWrap: true,          physics: AppConstant.physics,          slivers: [            SliverToBoxAdapter(              child: FutureBuilder(                future: Future.value(                    context.read<UpdateTaskCubit>().getTask(id, context)),                builder: (context, snapshot) {                  if (snapshot.hasData && snapshot.data != null) {                    final model = snapshot.data!;                    return BuildBodyViewTask(model: model);                  } else if (snapshot.hasError) {                    return Center(                      child: Text(                        "${snapshot.error}",                        style: Styles.text16(),                      ),                    );                  }                  return Container();                },              ),            ),          ],        );  }}