import 'package:flutter/material.dart';import 'package:intl/intl.dart';import '../../../../core/model/task_model.dart';import '../Widget/build_App_Bar_task_view.dart';import '../Widget/build_details_task_view.dart';import '../Widget/task_name_and_description.dart';class TaskScreen extends StatelessWidget {  final TaskModel model;  const TaskScreen({super.key, required this.model});  @override  Widget build(BuildContext context) => Scaffold(        appBar: buildAppBarTaskView(context, onTap: () {}),        body: _buildBody(),      );  Padding _buildBody() {    return Padding(        padding: const EdgeInsets.only(top: 30, right: 24, left: 24),        child: Column(          children: [            TaskNameAndDescription(model: model),            BuildDetailsTaskView(              value: DateFormat("E D").format(model.dateTime),              title: "Task Time :",              icons: Icons.timer,            ),            BuildDetailsTaskView(              iconsValue: Icon(                IconData(model.type.icon, fontFamily: "MaterialIcons"),                color: model.type.colorIcon,              ),              value: model.type.name,              title: "Task Category :",              icons: Icons.tag_rounded,            ),            BuildDetailsTaskView(              value: model.priority.number.toString(),              title: "Task Priority :",              icons: model.priority.flag,            ),            BuildDetailsTaskView(              value: "Add Sub - Task",              title: "Sub - Task",              icons: Icons.history_edu_rounded,            ),          ],        ),      );  }}