import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';class BuildDetailsTaskView extends StatelessWidget {  final String title;  final IconData icons;  final Icon? iconsValue;  final String ?value;  final Color ?items;  const BuildDetailsTaskView({    super.key,    required this.title,    required this.icons,    this.iconsValue,    this.value,    this.items  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 15),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Row(            children: [              Icon(                icons,                color: items ?? (AppColors.iconColor),              ),              10.horizontalSpace,              Text(                title,                style: Styles.text16().copyWith(color: items),              ),            ],          ),          if (value != null )            Container(              padding: const EdgeInsets.all(10),              height: 40.h,              color: AppColors.secondBackGroundColor,              child: Align(                  child: Row(                    children: [                      if (iconsValue != null)                        Padding(                          padding: const EdgeInsets.only(right: 15),                          child: iconsValue,                        ),                      if (value != null )                        Text(                          value ?? "",                          style: Styles.text12(),                        ),                    ],                  )),            )        ],      ),    );  }}