import 'package:flutter/material.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';class BuildDetailsTaskView extends StatelessWidget {  final String title;  final IconData icons;  final Icon? iconsValue;  final String value;  const BuildDetailsTaskView({    super.key,    required this.title,    required this.icons,    this.iconsValue,    required this.value,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 15),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Row(            children: [              Icon(                icons,                color: AppColors.iconColor,              ),              const SizedBox(                width: 10,              ),              Text(                title,                style: Styles.text16(context),              ),            ],          ),          Container(            padding: const EdgeInsets.all(10),            height: MediaQuery.sizeOf(context).height * .05,            color: AppColors.secondBackGroundColor,            child: Align(                child: Row(                  children: [                    if (iconsValue != null)                      Padding(                        padding: const EdgeInsets.only(right: 15),                        child: iconsValue,                      ),                    Text(                      value,                      style: Styles.text12(context),                    ),                  ],                )),          )        ],      ),    );  }}