import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/styles.dart';import '../../../Index/ViewModel/GetTask/get_task_cubit.dart';import 'build_details_task_view.dart';class DeleteTask extends StatelessWidget {  final String id;  const DeleteTask({super.key, required this.id});  @override  Widget build(BuildContext context) {    return InkWell(      onTap: () => _showDeleteConfirmationDialog(context),      child: BuildDetailsTaskView(        title: "Delete Task",        icons: Icons.delete_outlined,        items: Colors.red,      ),    );  }  void _showDeleteConfirmationDialog(BuildContext context) {    showDialog(      context: context,      builder: (BuildContext context) {        return AlertDialog(          backgroundColor: AppColors.secondBackGroundColor,          title: Text(            "Confirm Delete",            style: Styles.text16().copyWith(fontWeight: FontWeight.bold),          ),          content: Text("Are you sure you want to delete this task?",              style: Styles.text14().copyWith(                  fontWeight: FontWeight.normal,                  color: AppColors.gray1TextColor)),          actions: [            TextButton(              onPressed: () {                Navigator.of(context).pop();              },              child: const Text("Cancel"),            ),            TextButton(              onPressed: () {                context.read<GetTaskCubit>().deleteTask(context, id);                Navigator.of(context).pop();              },              child: const Text("Delete", style: TextStyle(color: Colors.red)),            ),          ],        );      },    );  }}