import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../core/model/task_model.dart';import '../../../../core/utils/resource/colors.dart';import '../../../../core/utils/resource/icons.dart';import '../../../../core/utils/resource/styles.dart';import '../../../TaskManagement/View/Widget/build_dialog_add_task.dart';import '../../../TaskManagement/ViewModel/AddTask/add_task_cubit.dart';class TaskNameAndDescription extends StatelessWidget {  const TaskNameAndDescription({    super.key,    required this.model,  });  final TaskModel model;  @override  Widget build(BuildContext context) {    return ListTile(      horizontalTitleGap: 5,      titleAlignment: ListTileTitleAlignment.threeLine,      contentPadding: EdgeInsets.zero,      leading: IconButton(          padding: EdgeInsets.only(bottom: 35),          onPressed: () {            model.isCompleted = !model.isCompleted;            context.read<AddTaskCubit>().filter();          },          icon: !model.isCompleted ? AppIcons.circle : AppIcons.checkCircle),      title: Text(        model.title,        style: Styles.text20AppBAr(),      ),      trailing: IconButton(          padding: EdgeInsets.only(bottom: 30),          onPressed: () {            showDialog(              barrierDismissible: false,              context: context,              builder: (context) => DialogAddTask(                taskModel: model,              ),            );          },          icon: const Icon(            Icons.edit,            color: Colors.white,          )),      subtitle: Padding(        padding: const EdgeInsets.only(top: 15),        child: Text(          model.description,          maxLines: model.description.length,          style: Styles.text16().copyWith(color: AppColors.gray1TextColor),        ),      ),    );  }}